mapper.select:
select distinct ah.*, s.supplier OrganName, p.Name HandsPersonName, u.username userName, a.Name AccountName
from jzh_account_head ah
left join jzh_account_item ai on ah.id=ai.header_id and ifnull(ai.delete_flag,'0') !='1'
left join jzh_depot_head dh on dh.id=ai.bill_id and ifnull(dh.delete_flag,'0') !='1'
left join jzh_supplier s on ah.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1'
left join jzh_user u on ah.creator=u.id and ifnull(u.Status,'0') !='1'
left join jzh_person p on ah.hands_person_id=p.id and ifnull(p.delete_Flag,'0') !='1'
left join jzh_account a on ah.account_id=a.id and ifnull(a.delete_Flag,'0') !='1'
where 1=1


    and ah.bill_no like #{bindBillNo}


    and ah.type=#{type}


    and ah.bill_time >= #{beginTime}


    and ah.bill_time <= #{endTime}


    and ah.organ_id=#{organId}


    and ah.hands_person_id=#{handsPersonId}


    and ah.creator=#{creator}


    and ah.creator in (

        #{creator}

    )


    and ah.account_id=#{accountId}


    and ah.status=#{status}



    and ah.remark like #{bindRemark}



        and ai.bill_id is null



        and dh.number like #{bindNumber}


and ifnull(ah.delete_flag,'0') !='1'
order by ah.id desc

    limit #{offset},#{rows}

SELECT
