select ah.*, s.supplier OrganName, p.Name HandsPersonName, a.Name AccountName from jzh_account_head ah left join jzh_supplier s on ah.organ_id=s.id and ifnull(s.delete_Flag,'0') !='1' left join jzh_person p on ah.hands_person_id=p.id and ifnull(p.delete_Flag,'0') !='1' left join jzh_account a on ah.account_id=a.id and ifnull(a.delete_Flag,'0') !='1' where 1=1
